import axios from "axios";

const INVESTMENT_BASE_API_URL = "http://localhost:80/investments/allinvestments";
const INVESTMENT_ADD = "http://localhost:80/investments/addinvestment";

class InvestmentService {

    getAllInvestments(){
        return axios.get(INVESTMENT_BASE_API_URL)
    }

    saveInvestment(investment){
        return axios.post(INVESTMENT_ADD, investment)
    }


}

export default new InvestmentService();
